/** @jsx jsx */
import { jsx, css } from '@emotion/core';
import React from 'react';
import courses from '../data/courses.json';

const list = css`
  @import url('https://fonts.googleapis.com/css?family=Montserrat&display=swap');
  font-family: 'Montserrat', sans-serif;
  margin-left: auto;
  margin-right: auto;
  width: 90%;

  .tooltip .tooltiptext {
    visibility: hidden;
    width: 120px;
    background-color: black;
    color: #fff;
    text-align: center;
    padding: 5px 0;
    border-radius: 6px;
    position: absolute;
    z-index: 1;
  }
  .tooltip {
    position: relative;
    display: inline-block;
  }
  .tooltip:hover .tooltiptext {
    visibility: visible;
  }
`;


function Courses() {
  return (
    <div css={list}>
      <ul>
      {courses && courses.map((course, key) => {
        return (
          <li>
            <p class="tooltip">{courses[key].courseId} - {courses[key].name}</p>
            <span class="tooltiptext">{courses[key].description}</span>
          </li>
        );
      })}
      </ul>
    </div>
  );
}

export default Courses;
