/** @jsx jsx */
import { jsx, css } from '@emotion/core';
import React from 'react';
import courses from '../data/courses.json';
import list from '../style/list.css';

const style = css`
  @import url('https://fonts.googleapis.com/css?family=Montserrat&display=swap');
  font-family: 'Montserrat', sans-serif;
  margin-left: auto;
  margin-right: auto;
  width: 90%;
`;


function Courses() {
  return (
    <div css={style}>
      <ul>
      {courses && courses.map((course, key) => {
        return (
          <li>
            <div class="tooltip">{courses[key].courseId} - {courses[key].name}
              <span class="tooltiptext" >{courses[key].description}</span>
            </div>
          </li>
        );
      })}
      </ul>
    </div>
  );
}

export default Courses;
